import { createSlice } from "@reduxjs/toolkit";

import { uiActions } from './ui-slice';

const cartSlice = createSlice({
    name: 'cart',
    initialState: {
        items: [],
        totalQuantity: 0,
        totalAmount: 0,
    },
    reducers: {
        addItemToCart(state, action) {
            const newItem = action.payload;
            const existingItem = state.items.find(item => item.id === newItem.id)
            state.totalQuantity++;
            //this adds a new item to our existing items array
            if (!existingItem) {
                state.items.push({
                    id: newItem.id,
                    price: newItem.price, 
                    quantity: 1,
                    totalPrice: newItem.price,
                    name: newItem.name,
                });

            } else {        //this will update amount of an existing item
                existingItem.quantity = existingItem.quantity + 1;
                existingItem.totalPrice = existingItem.totalPrice + newItem.price;
            }
        },
        removeItemFromCart(state, action) {
            const id = action.payload;
            //how many items are in the array?
            const existingItem = state.items.find(item => item.id === id);
            state.totalQuantity--;
            if (existingItem.quantity === 1) {
                state.items = state.items.filter(item => item.id !== id);
            } else {
                existingItem.quantity--;
                existingItem.totalPrice = existingItem.totalPrice - existingItem.price;
            }
        },
    },
});

export const sendCartData = (cart) => {
    return async (dispatch) => {
        //shows first that the data in the cart is pending being sent
        dispatch(
            uiActions.showNotification({
                status: "pending",
                title: "Sending...",
                message: "Sending cart data now!",
            })
        );

        const sendRequest = async () => {
          //then we send the data to the firebase backend
          const response = await fetch(
            "https://redux-test-project-2-default-rtdb.firebaseio.com/cart.json",
            { method: "PUT", body: JSON.stringify(cart) }
          );

          //is there an error?
          if (!response.ok) {
            throw new Error("Sending cart data failed!");
          }
        };

    try{
        await sendRequest();
        //notification that data send was successful
       dispatch(
        uiActions.showNotification({
          status: "success",
          title: "Success!",
          message: "Cart data has been sent successfully!",
        })
      );
    } catch (error) {
        //catch any error that may have occurred and show error message
        dispatch(
            uiActions.showNotification({
              status: "error",
              title: "Error!",
              message: "Sending cart data failed!",
            })
          );
    }

    };
};

export const cartActions = cartSlice.actions;

export default cartSlice;
